import { getConfigFilepath } from "@smithy/shared-ini-file-loader/dist-cjs/getConfigFilepath";
import { getSsoSessionData } from "@smithy/shared-ini-file-loader/dist-cjs/getSsoSessionData";
import { parseIni } from "@smithy/shared-ini-file-loader/dist-cjs/parseIni";
import { slurpFile } from "@smithy/shared-ini-file-loader/dist-cjs/slurpFile";
const swallowError = () => ({});
export const loadSsoSessionData = async (init = {}) => slurpFile(init.configFilepath ?? getConfigFilepath())
    .then(parseIni)
    .then(getSsoSessionData)
    .catch(swallowError);
