import { getChecksumConfiguration, resolveChecksumRuntimeConfig } from "@smithy/smithy-client/dist-cjs/extensions/checksum";
import { getRetryConfiguration, resolveRetryRuntimeConfig } from "@smithy/smithy-client/dist-cjs/extensions/retry";
export const getDefaultExtensionConfiguration = (runtimeConfig) => {
    return {
        ...getChecksumConfiguration(runtimeConfig),
        ...getRetryConfiguration(runtimeConfig),
    };
};
export const getDefaultClientConfiguration = getDefaultExtensionConfiguration;
export const resolveDefaultRuntimeConfig = (config) => {
    return {
        ...resolveChecksumRuntimeConfig(config),
        ...resolveRetryRuntimeConfig(config),
    };
};
